"code": "
const fs = require('fs');
const path = require('path');

const directoryPath = process.cwd();

fs.readdir(directoryPath, (err, files) => {
    if (err) {
        return console.log('Unable to scan directory: ' + err);
    } 

    files.forEach((file) => {
        let fileType = path.extname(file).slice(1);
        let newDir = path.join(directoryPath, fileType);

        fs.mkdir(newDir, { recursive: true }, (err) => {
            if (err) {
                return console.log('Unable to create directory: ' + err);
            }

            let oldPath = path.join(directoryPath, file);
            let newPath = path.join(newDir, file);

            fs.rename(oldPath, newPath, (err) => {
                if (err) {
                    return console.log('Unable to move file: ' + err);
                }
            });
        });
    });
});
"
}

